import { useNavigate, useSearchParams } from "react-router-dom";
import classes from "./AuthForm.module.css";
import useInput from "../../hooks/use-input";

export default function AuthForm() {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  const {
    inputVal: email,
    isValid: emailIsValid,
    hasErr: emailHasErr,
    touchFn: emailTouchFn,
    resetFn: emailResetFn,
    handleBlur: handleEmailBlur,
    handleChange: handleEmailChange,
  } = useInput((inputValue) => inputValue.includes("@"));

  const {
    inputVal: password,
    isValid: passwordIsValid,
    hasErr: passwordHasErr,
    touchFn: passwordTouchFn,
    resetFn: passwordResetFn,
    handleBlur: handlePasswordBlur,
    handleChange: handlePasswordChange,
  } = useInput((inputValue) => inputValue.trim().length > 6);

  let isFormValid=false;

  if(emailIsValid && passwordIsValid){
        isFormValid=true;
  }

  function handleSubmit(event) {
    event.preventDefault();

    emailTouchFn();
    passwordTouchFn();

    if(!isFormValid){
        return;
    }

    const form= new FormData(event);
    const formData=Object.fromEntries(form);

    console.log()

  }

  return (
    <div className={classes.container}>
      <form onSubmit={handleSubmit} className={classes.form}>
        <div className={classes["input-grp"]}>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onBlur={handleEmailBlur}
            onChange={handleEmailChange}
            placeholder="Enter email address"
          />
        </div>
        <div className={classes["input-grp"]}>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onBlur={handlePasswordBlur}
            onChange={handlePasswordChange}
            placeholder="Enter password"
          />
        </div>
        <div className={classes["form-actions"]}>
          <button className={classes.alternative} type="button">
            Register
          </button>
          <button className={classes.button}>Login</button>
        </div>
      </form>
    </div>
  );
}
